#include <iostream>
#include "def.glb"
#include "edge.h"



/************************************************************************
                     edge
*************************************************************************/


edge::edge(int f, int s)

   // Constructor.
{
   first = f;
   second = s;
}


/************************************************************************
                     ~edge
*************************************************************************/


edge::~edge()

   // Destructor.
{
}


/************************************************************************
                     get_first
*************************************************************************/


int  edge::get_first() 

   // Gets the first incident node of the edge.
{
   return first;
}


/************************************************************************
                     get_second
*************************************************************************/


int  edge::get_second()

   // Gets the second incident node of the edge.
{
   return second;
}


/************************************************************************
                     set_first
*************************************************************************/


void edge::set_first(int f)

   // Sets the first incident node of the edge.
{
   first = f;
}


/************************************************************************
                     set_second
*************************************************************************/


void edge::set_second(int s)

   // Sets the second incident node of the edge.
{
   second = s;
}
